import React from 'react';
import { NavLink } from 'react-router-dom';
import { House, UserCircle, ShieldCheck, Desktop, ClockClockwise, Files, NotePencil, Users, SignOut } from 'phosphor-react';

const Sidebar = ({ onLogout, user }) => {
  // Check if user has admin role
  const userRoles = user?.realmAccess?.roles || user?.realm_access?.roles || [];
  const isAdmin = userRoles.includes('admin') || userRoles.includes('realm-admin');

  const navItems = [
    { path: '/', icon: House, label: 'Dashboard' },
    { path: '/profile', icon: UserCircle, label: 'Profil Saya' },
    { path: '/security', icon: ShieldCheck, label: 'Keamanan' },
    { path: '/sessions', icon: Desktop, label: 'Sesi Aktif' },
    { path: '/logs', icon: ClockClockwise, label: 'Riwayat Login' },
    { path: '/documents', icon: Files, label: 'Dokumen' },
    { path: '/notes', icon: NotePencil, label: 'Catatan' },
  ];

  // Add User Management for admins only
  if (isAdmin) {
    navItems.push({ path: '/users', icon: Users, label: 'User Management' });
  }

  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <div className="logo-box">
          <img
            src="https://jogjaprov.go.id/storage/files/shares/page/1518066730_2d84b769e3cc9d6f06f8c91a6c3e285c.jpg"
            alt="Logo DIY"
          />
        </div>
        <div className="brand-text">
          <h1>PEMDA DIY</h1>
          <p>Account Console</p>
        </div>
      </div>

      <nav className="sidebar-nav">
        {navItems.map(({ path, icon: Icon, label }) => (
          <NavLink
            key={path}
            to={path}
            className={({ isActive }) => `nav-item ${isActive ? 'active' : ''}`}
          >
            <Icon size={24} weight="regular" />
            <span>{label}</span>
          </NavLink>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button className="logout-btn" onClick={onLogout}>
          <SignOut size={20} weight="regular" />
          <span>Keluar</span>
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
