/**
 * Documents Page  
 * Document upload, download, delete with pagination
 * Following frontend-developer and ui-ux-pro-max patterns
 */

import React, { useState, useEffect } from 'react';
import { FileArrowUp, FilePdf, FileDoc, File as FileIcon, Download, Trash, MagnifyingGlass } from 'phosphor-react';
import documentsService from '../services/documentsService';
import FileUploader from '../components/shared/FileUploader';
import Loading from '../components/shared/Loading';
import Pagination from '../components/shared/Pagination';
import ConfirmDialog from '../components/shared/ConfirmDialog';
import { showSuccess, showError } from '../hooks/useToast';

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 10;

  // Search
  const [searchQuery, setSearchQuery] = useState('');

  // Modal states
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [deleteConfirm, setDeleteConfirm] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  // Load documents on mount and page change
  useEffect(() => {
    loadDocuments();
    loadStats();
  }, [currentPage]);

  const loadDocuments = async () => {
    try {
      setLoading(true);
      const data = await documentsService.getDocuments({ page: currentPage, limit });
      setDocuments(data.documents || []);
      setTotalPages(data.totalPages || 1);
    } catch (error) {
      showError('Failed to load documents');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const data = await documentsService.getStats();
      setStats(data);
    } catch (error) {
      console.error('Failed to load stats');
    }
  };

  // Handle file selection from uploader
  const handleFileSelect = (file) => {
    setSelectedFile(file);
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!selectedFile) {
      showError('Please select a file');
      return;
    }

    setUploading(true);
    try {
      await documentsService.uploadDocument(
        selectedFile.file,
        (progress) => setUploadProgress(progress)
      );

      showSuccess('Document uploaded successfully');
      setShowUploadModal(false);
      setSelectedFile(null);
      setUploadProgress(0);
      loadDocuments();
      loadStats();
    } catch (error) {
      showError(error.message || 'Failed to upload document');
    } finally {
      setUploading(false);
    }
  };

  // Handle file download
  const handleDownload = async (doc) => {
    try {
      await documentsService.downloadDocument(doc._id, doc.originalName);
      showSuccess('Download started');
    } catch (error) {
      showError('Failed to download document');
    }
  };

  // Handle delete
  const handleDelete = async () => {
    if (!deleteConfirm) return;

    try {
      await documentsService.deleteDocument(deleteConfirm._id);
      showSuccess('Document deleted successfully');
      setDeleteConfirm(null);
      loadDocuments();
      loadStats();
    } catch (error) {
      showError('Failed to delete document');
    }
  };

  // Get file icon based on mime type
  const getFileIcon = (mimeType) => {
    if (mimeType.includes('pdf')) return <FilePdf size={32} weight="fill" color="#ef4444" />;
    if (mimeType.includes('word') || mimeType.includes('document')) return <FileDoc size={32} weight="fill" color="#3b82f6" />;
    return <FileIcon size={32} weight="fill" color="#64748b" />;
  };

  // Format bytes
  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  // Filter documents by search
  const filteredDocs = documents.filter(doc =>
    doc.originalName.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="documents-page">
      <div className="page-header">
        <div className="header-left">
          <h2>Documents</h2>
          <p>Upload and manage your documents</p>
        </div>
        <button className="btn-primary" onClick={() => setShowUploadModal(true)}>
          <FileArrowUp size={20} weight="bold" />
          <span>Upload Document</span>
        </button>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">
              <FileIcon size={24} weight="fill" />
            </div>
            <div className="stat-info">
              <span className="stat-value">{stats.totalDocuments || 0}</span>
              <span className="stat-label">Total Documents</span>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon secondary">
              <FileArrowUp size={24} weight="fill" />
            </div>
            <div className="stat-info">
              <span className="stat-value">{formatBytes(stats.totalSize || 0)}</span>
              <span className="stat-label">Total Size</span>
            </div>
          </div>
        </div>
      )}

      {/* Search */}
      <div className="search-box">
        <MagnifyingGlass size={20} />
        <input
          type="text"
          placeholder="Search documents..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Documents List */}
      {loading ? (
        <Loading message="Loading documents..." />
      ) : filteredDocs.length === 0 ? (
        <div className="empty-state">
          <FileIcon size={64} weight="light" />
          <h3>No documents found</h3>
          <p>Upload your first document to get started</p>
        </div>
      ) : (
        <>
          <div className="documents-list">
            {filteredDocs.map((doc) => (
              <div key={doc._id} className="document-item">
                <div className="doc-icon">
                  {getFileIcon(doc.mimeType)}
                </div>
                <div className="doc-info">
                  <h4>{doc.originalName}</h4>
                  <div className="doc-meta">
                    <span>{formatBytes(doc.size)}</span>
                    <span>•</span>
                    <span>{new Date(doc.uploadedAt).toLocaleDateString()}</span>
                  </div>
                </div>
                <div className="doc-actions">
                  <button
                    onClick={() => handleDownload(doc)}
                    className="icon-btn"
                    aria-label="Download document"
                  >
                    <Download size={20} />
                  </button>
                  <button
                    onClick={() => setDeleteConfirm(doc)}
                    className="icon-btn delete-btn"
                    aria-label="Delete document"
                  >
                    <Trash size={20} />
                  </button>
                </div>
              </div>
            ))}
          </div>

          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </>
      )}

      {/* Upload Modal */}
      {showUploadModal && (
        <div className="modal-overlay" onClick={() => !uploading && setShowUploadModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Upload Document</h3>
              <button onClick={() => !uploading && setShowUploadModal(false)} disabled={uploading}>×</button>
            </div>
            <div className="modal-body">
              <FileUploader
                onFileSelect={handleFileSelect}
                maxSize={10 * 1024 * 1024}
                acceptedTypes={[
                  'application/pdf',
                  'image/jpeg',
                  'image/png',
                  'application/msword',
                  'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
                ]}
                multiple={false}
              />

              {uploading && (
                <div className="upload-progress">
                  <div className="progress-bar">
                    <div className="progress-fill" style={{ width: `${uploadProgress}%` }} />
                  </div>
                  <span>{uploadProgress}%</span>
                </div>
              )}

              <div className="modal-actions">
                <button
                  type="button"
                  onClick={() => setShowUploadModal(false)}
                  className="btn-secondary"
                  disabled={uploading}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleUpload}
                  className="btn-primary"
                  disabled={!selectedFile || uploading}
                >
                  {uploading ? 'Uploading...' : 'Upload'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation */}
      <ConfirmDialog
        isOpen={!!deleteConfirm}
        title="Delete Document?"
        message={`Are you sure you want to delete "${deleteConfirm?.originalName}"? This action cannot be undone.`}
        variant="danger"
        confirmText="Delete"
        onConfirm={handleDelete}
        onCancel={() => setDeleteConfirm(null)}
      />
    </div>
  );
};

export default Documents;
