/**
 * User Management Page
 * Admin-only page for managing Keycloak users
 */

import React, { useState, useEffect } from 'react';
import {
    MagnifyingGlass, User, PencilSimple, X, CheckCircle, XCircle,
    Clock, Shield
} from 'phosphor-react';
import usersService from '../services/usersService';
import Loading from '../components/shared/Loading';
import { showSuccess, showError } from '../hooks/useToast';
import '../styles/UserManagement.css';

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [filteredUsers, setFilteredUsers] = useState([]);

    // Modal states
    const [showEditModal, setShowEditModal] = useState(false);
    const [editingUser, setEditingUser] = useState(null);
    const [userForm, setUserForm] = useState({
        firstName: '',
        lastName: '',
        email: '',
        enabled: true,
        emailVerified: false,
    });

    useEffect(() => {
        loadUsers();
    }, []);

    useEffect(() => {
        // Filter users based on search query
        if (searchQuery.trim()) {
            const query = searchQuery.toLowerCase();
            const filtered = users.filter(user =>
                user.username?.toLowerCase().includes(query) ||
                user.email?.toLowerCase().includes(query) ||
                user.firstName?.toLowerCase().includes(query) ||
                user.lastName?.toLowerCase().includes(query)
            );
            setFilteredUsers(filtered);
        } else {
            setFilteredUsers(users);
        }
    }, [searchQuery, users]);

    const loadUsers = async () => {
        try {
            setLoading(true);
            const data = await usersService.getUsers({ max: 100 });
            setUsers(data.users || []);
            setFilteredUsers(data.users || []);
        } catch (error) {
            showError('Failed to load users');
            console.error('Load users error:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleEditUser = (user) => {
        setEditingUser(user);
        setUserForm({
            firstName: user.firstName || '',
            lastName: user.lastName || '',
            email: user.email || '',
            enabled: user.enabled !== undefined ? user.enabled : true,
            emailVerified: user.emailVerified || false,
        });
        setShowEditModal(true);
    };

    const handleSaveUser = async (e) => {
        e.preventDefault();

        if (!userForm.email.trim()) {
            showError('Email is required');
            return;
        }

        try {
            await usersService.updateUser(editingUser.id, userForm);
            showSuccess('User updated successfully');
            setShowEditModal(false);
            setEditingUser(null);
            loadUsers(); // Reload user list
        } catch (error) {
            showError(error.response?.data?.message || 'Failed to update user');
        }
    };

    const formatDate = (timestamp) => {
        if (!timestamp) return 'N/A';
        return new Date(timestamp).toLocaleDateString('id-ID', {
            day: 'numeric',
            month: 'short',
            year: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) {
        return <Loading />;
    }

    return (
        <div className="user-management-page">
            {/* Header */}
            <div className="user-header">
                <div className="user-header-left">
                    <h2>User Management</h2>
                    <p>{users.length} total users</p>
                </div>
            </div>

            {/* Search */}
            <div className="user-search">
                <MagnifyingGlass size={20} />
                <input
                    type="text"
                    placeholder="Search users by name, email, or username..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
            </div>

            {/* Users Table */}
            <div className="users-table-container">
                <table className="users-table">
                    <thead>
                        <tr>
                            <th>User</th>
                            <th>Email</th>
                            <th>Status</th>
                            <th>Email Verified</th>
                            <th>Created</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredUsers.map(user => (
                            <tr key={user.id}>
                                <td>
                                    <div className="user-cell">
                                        <div className="user-avatar">
                                            {user.firstName?.[0] || user.username?.[0]?.toUpperCase() || 'U'}
                                        </div>
                                        <div className="user-info">
                                            <div className="user-name">
                                                {user.firstName || user.lastName
                                                    ? `${user.firstName} ${user.lastName}`.trim()
                                                    : user.username}
                                            </div>
                                            <div className="user-username">@{user.username}</div>
                                        </div>
                                    </div>
                                </td>
                                <td>{user.email || 'N/A'}</td>
                                <td>
                                    <span className={`status-badge ${user.enabled ? 'active' : 'inactive'}`}>
                                        {user.enabled ? (
                                            <>
                                                <CheckCircle size={14} weight="fill" />
                                                Active
                                            </>
                                        ) : (
                                            <>
                                                <XCircle size={14} weight="fill" />
                                                Disabled
                                            </>
                                        )}
                                    </span>
                                </td>
                                <td>
                                    <span className={`verify-badge ${user.emailVerified ? 'verified' : 'unverified'}`}>
                                        {user.emailVerified ? (
                                            <>
                                                <CheckCircle size={14} weight="fill" />
                                                Verified
                                            </>
                                        ) : (
                                            <>
                                                <Clock size={14} />
                                                Pending
                                            </>
                                        )}
                                    </span>
                                </td>
                                <td className="date-cell">{formatDate(user.createdTimestamp)}</td>
                                <td>
                                    <button
                                        className="icon-btn"
                                        onClick={() => handleEditUser(user)}
                                        title="Edit User"
                                    >
                                        <PencilSimple size={18} />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {filteredUsers.length === 0 && (
                    <div className="empty-table">
                        <User size={48} color="#cbd5e1" />
                        <p>No users found</p>
                    </div>
                )}
            </div>

            {/* Edit User Modal */}
            {showEditModal && editingUser && (
                <div className="modal-overlay" onClick={() => setShowEditModal(false)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h3>Edit User: {editingUser.username}</h3>
                            <button className="icon-btn" onClick={() => setShowEditModal(false)}>
                                <X size={24} />
                            </button>
                        </div>

                        <form onSubmit={handleSaveUser}>
                            <div className="modal-body">
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>First Name</label>
                                        <input
                                            type="text"
                                            className="form-input"
                                            value={userForm.firstName}
                                            onChange={(e) => setUserForm({ ...userForm, firstName: e.target.value })}
                                            placeholder="Enter first name"
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Last Name</label>
                                        <input
                                            type="text"
                                            className="form-input"
                                            value={userForm.lastName}
                                            onChange={(e) => setUserForm({ ...userForm, lastName: e.target.value })}
                                            placeholder="Enter last name"
                                        />
                                    </div>
                                </div>

                                <div className="form-group">
                                    <label>Email *</label>
                                    <input
                                        type="email"
                                        className="form-input"
                                        value={userForm.email}
                                        onChange={(e) => setUserForm({ ...userForm, email: e.target.value })}
                                        placeholder="user@example.com"
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <label className="checkbox-label">
                                        <input
                                            type="checkbox"
                                            checked={userForm.enabled}
                                            onChange={(e) => setUserForm({ ...userForm, enabled: e.target.checked })}
                                        />
                                        <span>Account Enabled</span>
                                    </label>
                                    <p className="form-hint">User can log in when enabled</p>
                                </div>

                                <div className="form-group">
                                    <label className="checkbox-label">
                                        <input
                                            type="checkbox"
                                            checked={userForm.emailVerified}
                                            onChange={(e) => setUserForm({ ...userForm, emailVerified: e.target.checked })}
                                        />
                                        <span>Email Verified</span>
                                    </label>
                                    <p className="form-hint">Mark email as verified</p>
                                </div>
                            </div>

                            <div className="modal-actions">
                                <button type="button" className="btn-secondary" onClick={() => setShowEditModal(false)}>
                                    Cancel
                                </button>
                                <button type="submit" className="btn-primary">
                                    Save Changes
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default UserManagement;
