/**
 * Notes Page
 * Notes CRUD with categories, tags, search, pagination
 * Following frontend-developer and ui-ux-pro-max patterns
 */

import React, { useState, useEffect } from 'react';
import { Plus, MagnifyingGlass, Note, PushPin, Trash, PencilSimple, Funnel } from 'phosphor-react';
import notesService from '../services/notesService';
import Loading from '../components/shared/Loading';
import Pagination from '../components/shared/Pagination';
import ConfirmDialog from '../components/shared/ConfirmDialog';
import { showSuccess, showError } from '../hooks/useToast';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const limit = 12;

  // Filters
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  // Modal states
  const [showNoteModal, setShowNoteModal] = useState(false);
  const [editingNote, setEditingNote] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState(null);

  // Form data
  const [noteForm, setNoteForm] = useState({
    title: '',
    content: '',
    category: 'Personal',
    tags: '',
  });

  // Load notes on mount and when filters change
  useEffect(() => {
    loadNotes();
    loadStats();
  }, [currentPage, selectedCategory]);

  const loadNotes = async () => {
    try {
      setLoading(true);
      const params = {
        page: currentPage,
        limit,
        category: selectedCategory !== 'all' ? selectedCategory : undefined,
      };

      const data = await notesService.getNotes(params);
      setNotes(data.notes || []);
      setTotalPages(data.totalPages || 1);
    } catch (error) {
      showError('Failed to load notes');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const data = await notesService.getStats();
      setStats(data);
    } catch (error) {
      console.error('Failed to load stats');
    }
  };

  // Handle create/update note
  const handleSaveNote = async (e) => {
    e.preventDefault();

    if (!noteForm.title.trim()) {
      showError('Title is required');
      return;
    }

    try {
      const noteData = {
        title: noteForm.title,
        content: noteForm.content,
        category: noteForm.category,
        tags: noteForm.tags.split(',').map(tag => tag.trim()).filter(Boolean),
      };

      if (editingNote) {
        await notesService.updateNote(editingNote._id, noteData);
        showSuccess('Note updated successfully');
      } else {
        await notesService.createNote(noteData);
        showSuccess('Note created successfully');
      }

      setShowNoteModal(false);
      resetForm();
      loadNotes();
      loadStats();
    } catch (error) {
      showError(error.message || 'Failed to save note');
    }
  };

  // Handle delete note
  const handleDeleteNote = async () => {
    if (!deleteConfirm) return;

    try {
      await notesService.deleteNote(deleteConfirm._id);
      showSuccess('Note deleted successfully');
      setDeleteConfirm(null);
      loadNotes();
      loadStats();
    } catch (error) {
      showError('Failed to delete note');
    }
  };

  // Open edit modal
  const openEditModal = (note) => {
    setEditingNote(note);
    setNoteForm({
      title: note.title,
      content: note.content || '',
      category: note.category,
      tags: note.tags?.join(', ') || '',
    });
    setShowNoteModal(true);
  };

  // Reset form
  const resetForm = () => {
    setNoteForm({
      title: '',
      content: '',
      category: 'Personal',
      tags: '',
    });
    setEditingNote(null);
  };

  // Filter notes by search
  const filteredNotes = notes.filter(note =>
    note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    note.content?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const categories = ['Personal', 'Work', 'Ideas', 'Important', 'Other'];

  return (
    <div className="notes-page">
      <div className="page-header">
        <div className="header-left">
          <h2>My Notes</h2>
          <p>Manage your personal notes</p>
        </div>
        <button
          className="btn-primary"
          onClick={() => {
            resetForm();
            setShowNoteModal(true);
          }}
        >
          <Plus size={20} weight="bold" />
          <span>New Note</span>
        </button>
      </div>

      {/* Stats Cards */}
      {stats && (
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">
              <Note size={24} weight="fill" />
            </div>
            <div className="stat-info">
              <span className="stat-value">{stats.total || 0}</span>
              <span className="stat-label">Total Notes</span>
            </div>
          </div>
          {stats.byCategory && Object.entries(stats.byCategory).map(([cat, count]) => (
            <div key={cat} className="stat-card">
              <div className="stat-icon secondary">
                <Funnel size={24} weight="fill" />
              </div>
              <div className="stat-info">
                <span className="stat-value">{count}</span>
                <span className="stat-label">{cat}</span>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Search and Filters */}
      <div className="notes-controls">
        <div className="search-box">
          <MagnifyingGlass size={20} />
          <input
            type="text"
            placeholder="Search notes..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <select
          value={selectedCategory}
          onChange={(e) => {
            setSelectedCategory(e.target.value);
            setCurrentPage(1);
          }}
          className="category-filter"
        >
          <option value="all">All Categories</option>
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      {/* Notes Grid */}
      {loading ? (
        <Loading message="Loading notes..." />
      ) : filteredNotes.length === 0 ? (
        <div className="empty-state">
          <Note size={64} weight="light" />
          <h3>No notes found</h3>
          <p>Create your first note to get started</p>
        </div>
      ) : (
        <>
          <div className="notes-grid">
            {filteredNotes.map((note) => (
              <div key={note._id} className="note-card">
                <div className="note-header">
                  <span className={`category-badge ${note.category.toLowerCase()}`}>
                    {note.category}
                  </span>
                  <div className="note-actions">
                    <button
                      onClick={() => openEditModal(note)}
                      aria-label="Edit note"
                    >
                      <PencilSimple size={18} />
                    </button>
                    <button
                      onClick={() => setDeleteConfirm(note)}
                      className="delete-btn"
                      aria-label="Delete note"
                    >
                      <Trash size={18} />
                    </button>
                  </div>
                </div>

                <h4>{note.title}</h4>
                {note.content && (
                  <p className="note-content">{note.content}</p>
                )}

                {note.tags && note.tags.length > 0 && (
                  <div className="note-tags">
                    {note.tags.map((tag, idx) => (
                      <span key={idx} className="tag">{tag}</span>
                    ))}
                  </div>
                )}

                <div className="note-footer">
                  <span className="note-date">
                    {new Date(note.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
            ))}
          </div>

          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </>
      )}

      {/* Note Modal */}
      {showNoteModal && (
        <div className="modal-overlay" onClick={() => setShowNoteModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{editingNote ? 'Edit Note' : 'Create New Note'}</h3>
              <button onClick={() => setShowNoteModal(false)}>Ã—</button>
            </div>
            <form onSubmit={handleSaveNote} className="modal-body">
              <div className="form-group">
                <label htmlFor="title">Title *</label>
                <input
                  type="text"
                  id="title"
                  value={noteForm.title}
                  onChange={(e) => setNoteForm({ ...noteForm, title: e.target.value })}
                  placeholder="Enter note title"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="category">Category</label>
                <select
                  id="category"
                  value={noteForm.category}
                  onChange={(e) => setNoteForm({ ...noteForm, category: e.target.value })}
                >
                  {categories.map(cat => (
                    <option key={cat} value={cat}>{cat}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="content">Content</label>
                <textarea
                  id="content"
                  value={noteForm.content}
                  onChange={(e) => setNoteForm({ ...noteForm, content: e.target.value })}
                  placeholder="Enter note content"
                  rows={6}
                />
              </div>

              <div className="form-group">
                <label htmlFor="tags">Tags (comma-separated)</label>
                <input
                  type="text"
                  id="tags"
                  value={noteForm.tags}
                  onChange={(e) => setNoteForm({ ...noteForm, tags: e.target.value })}
                  placeholder="e.g. important, work, reminder"
                />
              </div>

              <div className="modal-actions">
                <button type="button" onClick={() => setShowNoteModal(false)} className="btn-secondary">
                  Cancel
                </button>
                <button type="submit" className="btn-primary">
                  {editingNote ? 'Update Note' : 'Create Note'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation */}
      <ConfirmDialog
        isOpen={!!deleteConfirm}
        title="Delete Note?"
        message={`Are you sure you want to delete "${deleteConfirm?.title}"? This action cannot be undone.`}
        variant="danger"
        confirmText="Delete"
        onConfirm={handleDeleteNote}
        onCancel={() => setDeleteConfirm(null)}
      />
    </div>
  );
};

export default Notes;
