/**
 * PEMDA DIY Dashboard - React App
 * Main Application Component
 */

import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import keycloak from './keycloak';

// Pages
import Dashboard from './pages/Dashboard';
import Profile from './pages/Profile';
import Security from './pages/Security';
import Sessions from './pages/Sessions';
import Logs from './pages/Logs';
import Documents from './pages/Documents';
import Notes from './pages/Notes';
import Login from './pages/Login';
import Callback from './pages/Callback';

// Components
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Loading from './components/Loading';
import ErrorBoundary from './components/shared/ErrorBoundary';

// Styles
import './styles/App.css';

function App() {
  // Always require Keycloak authentication - no dev mode bypass
  const [authenticated, setAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  // Use ref to prevent double initialization
  const keycloakInitialized = useRef(false);

  useEffect(() => {
    // Guard against double initialization
    if (keycloakInitialized.current) {
      console.log('âš ï¸ Keycloak already initialized, skipping...');
      return;
    }

    console.log('ðŸ”‘ Initializing Keycloak...');
    console.log('ðŸ”‘ Client ID:', 'pemda-dashboard');
    console.log('ðŸ”‘ Keycloak URL:', 'http://10.7.183.46:8080');
    console.log('ðŸ¢ Realm:', 'Jogja-SSO');
    console.log('ðŸ“ Current URL:', window.location.href);

    // Check for authorization code in URL
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
      console.log('âœ… Authorization code detected:', code.substring(0, 10) + '...');
    } else {
      console.log('â„¹ï¸ No code in URL - first visit');
    }

    keycloakInitialized.current = true;

    // Initialize Keycloak with proper OIDC flow
    keycloak.init({
      onLoad: 'login-required', // Automatically redirect to Keycloak if not authenticated
      pkceMethod: 'S256',
      checkLoginIframe: false,
      redirectUri: window.location.origin,
      silentCheckSsoRedirectUri: window.location.origin + '/silent-check-sso.html'
    }).then(authenticated => {
      console.log('âœ… Keycloak init promise resolved');
      console.log('ðŸ” authenticated (parameter):', authenticated);
      console.log('ðŸ” keycloak.authenticated:', keycloak.authenticated);
      console.log('ðŸ” keycloak.token:', keycloak.token ? keycloak.token.substring(0, 30) + '...' : 'NULL');
      console.log('ðŸ”‘ Client ID:', keycloak.clientId);
      console.log('ðŸ”‘ Realm:', keycloak.realm);

      if (authenticated && keycloak.token) {
        console.log('ðŸŽ« Token received successfully!');
        console.log('ðŸŽ« Token expires:', new Date(keycloak.tokenParsed?.exp * 1000).toLocaleString());

        setAuthenticated(true);
        console.log('âœ… State: authenticated = true');

        // Load user profile
        console.log('ðŸ‘¤ Loading user profile...');
        keycloak.loadUserProfile().then(profile => {
          setUser({
            id: keycloak.tokenParsed.sub,
            username: keycloak.tokenParsed.preferred_username,
            email: profile.email,
            firstName: profile.firstName,
            lastName: profile.lastName,
            fullName: `${profile.firstName || ''} ${profile.lastName || ''}`.trim()
          });
          console.log('âœ… User profile loaded:', keycloak.tokenParsed.preferred_username);
          console.log('âœ… Loading complete - rendering Dashboard');
          setLoading(false);
        }).catch(err => {
          console.error('âŒ Failed to load user profile:', err);
          setLoading(false);
        });
      } else {
        console.log('ðŸ”“ Not authenticated after init');
        console.log('ðŸ” Reason: authenticated=' + authenticated + ', token=' + (keycloak.token ? 'exists' : 'NULL'));
        setAuthenticated(false);
        setLoading(false);
      }
    }).catch(error => {
      console.error('âŒ Keycloak initialization error:', error);
      keycloakInitialized.current = false; // Reset on error to allow retry
      setAuthenticated(false);
      setLoading(false);
    });

    // Token refresh handler
    keycloak.onTokenExpired = () => {
      console.log('â° Token expired, refreshing...');
      keycloak.updateToken(30).then(refreshed => {
        if (refreshed) {
          console.log('âœ… Token refreshed:', keycloak.token.substring(0, 20) + '...');
        } else {
          console.log('â„¹ï¸ Token still valid');
        }
      }).catch(() => {
        console.error('âŒ Failed to refresh token - logging out');
        keycloak.logout({
          redirectUri: window.location.origin
        });
      });
    };
  }, []);

  const handleLogin = () => {
    console.log('ðŸ” Login called - Redirecting to Keycloak...');
    console.log('ðŸ“ Redirect URI:', window.location.origin);
    console.log('ðŸ”‘ Client ID:', keycloak.clientId);

    keycloak.login({
      redirectUri: window.location.origin,
      prompt: 'login'
    });
  };

  const handleLogout = () => {
    console.log('ðŸšª Logout called');
    keycloak.logout({
      redirectUri: window.location.origin
    });
  };

  if (loading) {
    console.log('ðŸ”„ Rendering: Loading screen');
    return <Loading />;
  }

  if (!authenticated) {
    console.log('ðŸ”“ Rendering: Login page (authenticated=' + authenticated + ')');
    return (
      <ErrorBoundary>
        <Router>
          <Routes>
            <Route path="/callback" element={<Callback />} />
            <Route path="*" element={<Login onLogin={handleLogin} />} />
          </Routes>
          <Toaster position="top-right" />
        </Router>
      </ErrorBoundary>
    );
  }

  return (
    <ErrorBoundary>
      <Router>
        <div className="dashboard-container">
          <Sidebar onLogout={handleLogout} />

          <main className="main-content">
            <Header user={user} />

            <Routes>
              <Route path="/" element={<Dashboard user={user} />} />
              <Route path="/profile" element={<Profile user={user} />} />
              <Route path="/security" element={<Security />} />
              <Route path="/sessions" element={<Sessions />} />
              <Route path="/logs" element={<Logs />} />
              <Route path="/documents" element={<Documents />} />
              <Route path="/notes" element={<Notes />} />
              <Route path="/callback" element={<Navigate to="/" replace />} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </main>
        </div>

        <Toaster position="top-right" />
      </Router>
    </ErrorBoundary>
  );
}

export default App;
