/**
 * Stats Service
 * Fetch dashboard statistics from backend
 */

import api from './api';

const statsService = {
    /**
     * Get notes statistics
     */
    getNotesStats: async () => {
        try {
            const response = await api.get('/notes/stats');
            return response.data;
        } catch (error) {
            console.error('Failed to load notes stats:', error);
            return { total: 0, recent: [] };
        }
    },

    /**
     * Get documents statistics
     */
    getDocumentsStats: async () => {
        try {
            const response = await api.get('/documents/stats');
            return response.data;
        } catch (error) {
            console.error('Failed to load documents stats:', error);
            return { total: 0, totalSize: 0 };
        }
    },

    /**
     * Get sessions statistics
     */
    getSessionsStats: async () => {
        try {
            const response = await api.get('/sessions');
            return { active: response.data.sessions?.length || 0 };
        } catch (error) {
            console.error('Failed to load sessions stats:', error);
            return { active: 0 };
        }
    }
};

export default statsService;
