import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShieldCheck, Desktop, Files, NotePencil } from 'phosphor-react';

const Dashboard = ({ user }) => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    security: 85,
    sessions: 0,
    documents: 0,
    notes: 0,
    loading: true
  });

  useEffect(() => {
    // Fetch real stats from backend
    const fetchStats = async () => {
      try {
        const accessToken = localStorage.getItem('access_token');
        if (!accessToken) {
          setStats(prev => ({ ...prev, loading: false }));
          return;
        }

        // Fetch sessions count
        const sessionsRes = await fetch('http://localhost:5000/api/sessions', {
          headers: { 'Authorization': `Bearer ${accessToken}` }
        });
        const sessionsData = await sessionsRes.json();

        // Fetch documents stats
        const docsRes = await fetch('http://localhost:5000/api/documents/stats', {
          headers: { 'Authorization': `Bearer ${accessToken}` }
        });
        const docsData = await docsRes.json();

        // Fetch notes stats
        const notesRes = await fetch('http://localhost:5000/api/notes/stats', {
          headers: { 'Authorization': `Bearer ${accessToken}` }
        });
        const notesData = await notesRes.json();

        setStats({
          security: 85, // TODO: Implement security score calculation
          sessions: sessionsData.sessions?.length || 0,
          documents: docsData.totalDocuments || 0,
          notes: notesData.total || 0,
          loading: false
        });
      } catch (error) {
        console.error('Failed to fetch stats:', error);
        setStats(prev => ({ ...prev, loading: false }));
      }
    };

    fetchStats();
  }, []);

  const handleProfileClick = () => {
    navigate('/profile');
  };

  // Get user initials for avatar
  const getUserInitials = () => {
    if (!user) return 'U';
    const firstName = user.firstName || '';
    const lastName = user.lastName || '';
    return (firstName.charAt(0) + lastName.charAt(0)).toUpperCase() || 'U';
  };

  return (
    <div className="dashboard-page">
      {/* Hero Banner with Profile Widget */}
      <div
        className="hero-banner"
        style={{ backgroundImage: 'url(/hero-banner.png)' }}
      >
        <div className="hero-content">
          <h1>Selamat Datang di PEMDA DIY Dashboard</h1>
          <p>Sistem Manajemen Terintegrasi untuk Pemerintah Daerah Yogyakarta</p>
        </div>

        {/* Interactive Profile Widget */}
        <div className="profile-widget" onClick={handleProfileClick}>
          <div className="profile-widget-header">
            <div className="profile-avatar-large">
              {user?.profilePicture ? (
                <img src={user.profilePicture} alt={`${user.firstName} ${user.lastName}`} />
              ) : (
                getUserInitials()
              )}
            </div>
            <div className="profile-widget-info">
              <div className="profile-widget-name">
                {user?.firstName} {user?.lastName || 'User'}
              </div>
              <div className="profile-widget-email">
                {user?.email || 'user@example.com'}
              </div>
            </div>
          </div>

          <div className="profile-widget-stats">
            <div className="profile-stat-item">
              <span className="profile-stat-value">{stats.sessions}</span>
              <span className="profile-stat-label">Sesi</span>
            </div>
            <div className="profile-stat-item">
              <span className="profile-stat-value">{stats.documents}</span>
              <span className="profile-stat-label">Dokumen</span>
            </div>
            <div className="profile-stat-item">
              <span className="profile-stat-value">{stats.notes}</span>
              <span className="profile-stat-label">Catatan</span>
            </div>
          </div>
        </div>
      </div>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        {/* Stats Grid with Real Data */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #0ea5e9, #0284c7)' }}>
              <ShieldCheck size={32} weight="fill" color="white" />
            </div>
            <div className="stat-content">
              <h3>{stats.loading ? '...' : `${stats.security}%`}</h3>
              <p>Skor Keamanan</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #10b981, #059669)' }}>
              <Desktop size={32} weight="fill" color="white" />
            </div>
            <div className="stat-content">
              <h3>{stats.loading ? '...' : stats.sessions}</h3>
              <p>Sesi Aktif</p>
            </div>
          </div>

          <div className="stat-card" onClick={() => navigate('/documents')}>
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #f59e0b, #d97706)' }}>
              <Files size={32} weight="fill" color="white" />
            </div>
            <div className="stat-content">
              <h3>{stats.loading ? '...' : stats.documents}</h3>
              <p>Dokumen</p>
            </div>
          </div>

          <div className="stat-card" onClick={() => navigate('/notes')}>
            <div className="stat-icon" style={{ background: 'linear-gradient(135deg, #8b5cf6, #7c3aed)' }}>
              <NotePencil size={32} weight="fill" color="white" />
            </div>
            <div className="stat-content">
              <h3>{stats.loading ? '...' : stats.notes}</h3>
              <p>Catatan</p>
            </div>
          </div>
        </div>

        {/* Welcome Message */}
        <div className="dashboard-welcome">
          <h3>Dashboard Anda Siap Digunakan! ðŸŽ‰</h3>
          <p>Semua fitur telah terhubung dengan Keycloak dan backend.</p>
          <p>Gunakan menu di sidebar untuk navigasi ke fitur-fitur lainnya.</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
