/**
 * API Service - Base Axios Configuration
 * Following frontend-developer and react-best-practices patterns
 * Handles Keycloak token injection, error handling, and request/response transformations
 */

import axios from 'axios';
import keycloak from '../keycloak';

// Create axios instance with base configuration
const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000',
    timeout: 30000, // 30 seconds
    headers: {
        'Content-Type': 'application/json',
    },
});

// Request interceptor - Attach token from localStorage
api.interceptors.request.use(
    (config) => {
        // Skip auth for health checks
        if (config.url === '/health') {
            return config;
        }

        // Get token from localStorage (backend proxy auth)
        const accessToken = localStorage.getItem('access_token');

        if (accessToken) {
            config.headers.Authorization = `Bearer ${accessToken}`;
            console.log('ðŸ” [API] Token injected from localStorage');
        } else {
            console.warn('âš ï¸ [API] No token in localStorage - request may fail');
        }

        return config;
    },
    (error) => {
        console.error('[API] Request error:', error);
        return Promise.reject(error);
    }
);

// Response interceptor - Handle errors globally
api.interceptors.response.use(
    (response) => {
        // Successful response - return data directly
        return response;
    },
    async (error) => {
        const originalRequest = error.config;

        // Handle 401 Unauthorized - Token expired
        if (error.response?.status === 401 && !originalRequest._retry) {
            originalRequest._retry = true;

            try {
                // Attempt to refresh Keycloak token
                if (keycloak.updateToken) {
                    const refreshed = await keycloak.updateToken(30); // Refresh if expiring in 30s

                    if (refreshed) {
                        console.log('âœ… [API] Token refreshed');
                        // Retry original request with new token
                        originalRequest.headers.Authorization = `Bearer ${keycloak.token}`;
                        return api(originalRequest);
                    }
                }
            } catch (refreshError) {
                console.error('[API] Token refresh failed:', refreshError);
                // Redirect to login if refresh fails
                if (keycloak.login) {
                    keycloak.login();
                }
                return Promise.reject(refreshError);
            }
        }

        // Handle specific error statuses
        const errorMessage = error.response?.data?.message || error.message;
        const errorStatus = error.response?.status;

        console.error(`[API] Error ${errorStatus}:`, errorMessage);

        // Format error for consistent handling
        const formattedError = {
            status: errorStatus,
            message: errorMessage,
            data: error.response?.data,
            originalError: error,
        };

        return Promise.reject(formattedError);
    }
);

/**
 * Helper function to handle file downloads
 */
export const downloadFile = async (url, filename) => {
    try {
        const response = await api.get(url, {
            responseType: 'blob', // Important for file downloads
        });

        // Create blob link to download
        const blob = new Blob([response.data]);
        const link = document.createElement('a');
        link.href = window.URL.createObjectURL(blob);
        link.download = filename || 'download';
        link.click();

        // Cleanup
        window.URL.revokeObjectURL(link.href);

        return { success: true };
    } catch (error) {
        console.error('[API] Download error:', error);
        throw error;
    }
};

/**
 * Helper function to upload files with multipart/form-data
 */
export const uploadFile = async (url, file, onUploadProgress) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
        const response = await api.post(url, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
            onUploadProgress: (progressEvent) => {
                if (onUploadProgress) {
                    const percentCompleted = Math.round(
                        (progressEvent.loaded * 100) / progressEvent.total
                    );
                    onUploadProgress(percentCompleted);
                }
            },
        });

        return response.data;
    } catch (error) {
        console.error('[API] Upload error:', error);
        throw error;
    }
};

export default api;
